import marked from 'marked';
/* Cannot fix error when importing using Sapper */
// import { client } from '../../../sapper/node_modules/stancy/dist/index.js';

class Client {
	constructor() {
		this._config = {};
	}
	_process(data, callback) {
		data = JSON.parse(data);
		if (callback.preprocess) {
			if (Array.isArray(data)) {
				data.map((item) => {
					if (item) {
						item = callback.preprocess({ item: item });
					}
				});
			} else {
				if (data) {
					data = callback.preprocess({ item: data });
				}
			}
			return data;
		} else {
			return data;
		}
	}
	config(value) {
		this._config = value;
	}
	fetch(path) {
		var config = this._config;

		const fetch = process.browser ? window.fetch : require('node-fetch').default;

		var base = config.preview || 'http://localhost:3000/api/';

		if (process.env.NODE_ENV === 'development') {
			if (config.preview) {
				base = config.preview;
			}
			base = config.preview;
		} else {
			base = config.production;
		}

		return fetch(`${base}${path}`).then((r) => r.text()).then((json) => {
			try {
				var result = this._process(json, config);
				return result;
			} catch (err) {
				return json;
			}
		});
	}
}

var client = new Client();

client.config({
	preview: 'http://localhost:4000/api/',
	production: 'https://now-restlike-api.now.sh/api/',
	preprocess: ({ item }) => {
		if (item.content) item.content = marked(item.content);
		return item;
	}
});

export default client;
